import{_ as e,c as l,o as a,ae as d}from"./chunks/framework.Ck3L_38O.js";const g=JSON.parse('{"title":"AstrBot 插件 - 五子棋游戏（全局匹配重构版）","description":"","frontmatter":{},"headers":[],"relativePath":"五子棋游戏.md","filePath":"五子棋游戏.md"}'),r={name:"五子棋游戏.md"};function i(s,t,n,f,x,o){return a(),l("div",null,[...t[0]||(t[0]=[d('<h1 id="astrbot-插件-五子棋游戏-全局匹配重构版" tabindex="-1">AstrBot 插件 - 五子棋游戏（全局匹配重构版） <a class="header-anchor" href="#astrbot-插件-五子棋游戏-全局匹配重构版" aria-label="Permalink to &quot;AstrBot 插件 - 五子棋游戏（全局匹配重构版）&quot;">​</a></h1><h2 id="用途" tabindex="-1">用途 <a class="header-anchor" href="#用途" aria-label="Permalink to &quot;用途&quot;">​</a></h2><p>这是一个简易的五子棋游戏插件，适用于 AstrBot 平台。用户可以通过该插件在群聊中进行五子棋对战，支持玩家对战和人机对战，并提供排行榜功能记录战绩。</p><h2 id="基本功能" tabindex="-1">基本功能 <a class="header-anchor" href="#基本功能" aria-label="Permalink to &quot;基本功能&quot;">​</a></h2><ul><li>开始五子棋游戏，支持玩家对战或与 AI 对战。</li><li>支持玩家跨群聊实时对局。</li><li>通过图片展示棋盘，方便查看游戏进程。</li><li>支持夸群聊落子、悔棋、认输、求和等操作。</li><li>提供排行榜和个人战绩查询。</li></ul><h2 id="使用命令" tabindex="-1">使用命令 <a class="header-anchor" href="#使用命令" aria-label="Permalink to &quot;使用命令&quot;">​</a></h2><p>【游戏指令】</p><table tabindex="0"><thead><tr><th style="text-align:left;">命令</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">/五子棋帮助</td><td style="text-align:left;">快速查看五子棋菜单</td></tr><tr><td style="text-align:left;">/五子棋</td><td style="text-align:left;">开始一局新的五子棋游戏，黑棋先手。</td></tr><tr><td style="text-align:left;">/加入五子棋 &lt;游戏id&gt;</td><td style="text-align:left;">加入指定游戏，成为白棋玩家。（可跨群聊）</td></tr><tr><td style="text-align:left;">/人机对战</td><td style="text-align:left;">开局后与 AI 对战，AI 作为白棋玩家。</td></tr><tr><td style="text-align:left;">/取消五子棋</td><td style="text-align:left;">游戏未开始时，发起者可取消游戏。</td></tr><tr><td style="text-align:left;">直接输入坐标（如 h7）</td><td style="text-align:left;">在指定位置落子，坐标格式为字母+数字（如 H7）。</td></tr><tr><td style="text-align:left;">/查看棋局</td><td style="text-align:left;">查看当前棋盘状态。</td></tr><tr><td style="text-align:left;">/悔棋</td><td style="text-align:left;">申请悔棋，撤销上一步（或两步，若对手已下棋），每局限成功一次，最多申请三次。</td></tr><tr><td style="text-align:left;">/接受悔棋</td><td style="text-align:left;">接受对方的悔棋请求。</td></tr><tr><td style="text-align:left;">/拒绝悔棋</td><td style="text-align:left;">拒绝对方的悔棋请求。</td></tr><tr><td style="text-align:left;">/求和</td><td style="text-align:left;">提出平局请求。</td></tr><tr><td style="text-align:left;">/接受求和</td><td style="text-align:left;">接受对方的平局请求。</td></tr><tr><td style="text-align:left;">/拒绝求和</td><td style="text-align:left;">拒绝对方的平局请求。</td></tr><tr><td style="text-align:left;">/认输</td><td style="text-align:left;">主动认输，结束游戏。</td></tr><tr><td style="text-align:left;">/结束下棋</td><td style="text-align:left;">[仅限人机对战] 放弃对局（无胜负记录）</td></tr></tbody></table><p>【战绩查询】</p><table tabindex="0"><thead><tr><th style="text-align:left;">命令</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">/五子棋排行榜</td><td style="text-align:left;">查看五子棋游戏排行榜（按胜场数排序）。</td></tr><tr><td style="text-align:left;">/我的战绩</td><td style="text-align:left;">查看个人五子棋战绩。</td></tr></tbody></table>',10)])])}const h=e(r,[["render",i]]);export{g as __pageData,h as default};
