import{_ as e,c as i,o,ae as l}from"./chunks/framework.Ck3L_38O.js";const u=JSON.parse('{"title":"🪐 JM-Cosmos","description":"","frontmatter":{},"headers":[],"relativePath":"禁漫搜索.md","filePath":"禁漫搜索.md"}'),t={name:"禁漫搜索.md"};function s(d,a,n,c,h,r){return o(),i("div",null,[...a[0]||(a[0]=[l('<h1 id="🪐-jm-cosmos" tabindex="-1"><div align="center">🪐 JM-Cosmos</div> <a class="header-anchor" href="#🪐-jm-cosmos" aria-label="Permalink to &quot;&lt;div align=&quot;center&quot;&gt;🪐 JM-Cosmos&lt;/div&gt;&quot;">​</a></h1><div align="center"><em>全能型 JM 漫画下载与管理工具</em></div><h2 id="◎-介绍" tabindex="-1">◎ 介绍 <a class="header-anchor" href="#◎-介绍" aria-label="Permalink to &quot;◎ 介绍&quot;">​</a></h2><p>JM-Cosmos 是一个基于 AstrBot 开发的 JM 漫画下载插件，支持漫画搜索、预览、下载、PDF 转换与 QQ 发送</p><h2 id="◎-功能特性" tabindex="-1">◎ 功能特性 <a class="header-anchor" href="#◎-功能特性" aria-label="Permalink to &quot;◎ 功能特性&quot;">​</a></h2><h3 id="核心功能" tabindex="-1">核心功能 <a class="header-anchor" href="#核心功能" aria-label="Permalink to &quot;核心功能&quot;">​</a></h3><ul><li>通过 ID 下载 JM 漫画并转换为 PDF</li><li>支持发送漫画前几页图片预览</li><li>支持搜索漫画、作者作品</li><li>支持获取漫画排行榜和随机推荐</li></ul><h3 id="高级功能" tabindex="-1">高级功能 <a class="header-anchor" href="#高级功能" aria-label="Permalink to &quot;高级功能&quot;">​</a></h3><ul><li>自动测试和更新可用域名</li><li>提供详细的 PDF 文件信息和诊断</li><li>可配置代理、Cookie 和线程数</li><li>智能适配各种命名格式的漫画目录</li><li>强大的错误处理和故障恢复能力</li><li>自动处理域名失效问题</li><li>线程池和内存优化</li><li>支持多种搜索格式和批量显示</li></ul><h2 id="◎-命令列表" tabindex="-1">◎ 命令列表 <a class="header-anchor" href="#◎-命令列表" aria-label="Permalink to &quot;◎ 命令列表&quot;">​</a></h2><h3 id="基础命令" tabindex="-1">基础命令 <a class="header-anchor" href="#基础命令" aria-label="Permalink to &quot;基础命令&quot;">​</a></h3><ul><li><code>/jm [ID]</code> - 下载漫画为 PDF 并发送</li><li><code>/jmimg [ID] [页数]</code> - 发送漫画前几页图片预览</li><li><code>/jminfo [ID]</code> - 查看漫画信息</li><li><code>/jmpdf [ID]</code> - 检查 PDF 文件信息</li><li><code>/jmhelp</code> - 查看帮助信息</li></ul><h3 id="搜索功能" tabindex="-1">搜索功能 <a class="header-anchor" href="#搜索功能" aria-label="Permalink to &quot;搜索功能&quot;">​</a></h3><ul><li><code>/jmsearch [关键词] [序号]</code> - 搜索漫画</li><li><code>/jmauthor [作者] [数量]</code> - 搜索作者作品（显示前 N 部或详细信息）</li><li><code>/jmrecommend</code> - 随机推荐漫画</li></ul><h3 id="配置命令" tabindex="-1">配置命令 <a class="header-anchor" href="#配置命令" aria-label="Permalink to &quot;配置命令&quot;">​</a></h3><ul><li><code>/jmconfig proxy [代理URL]</code> - 设置代理 URL</li><li><code>/jmconfig noproxy</code> - 清除代理设置</li><li><code>/jmconfig cookie [AVS Cookie]</code> - 设置登录 Cookie</li><li><code>/jmconfig threads [数量]</code> - 设置最大下载线程数</li><li><code>/jmconfig domain [域名]</code> - 添加 JM 漫画域名</li><li><code>/jmconfig debug [on/off]</code> - 开启/关闭调试模式</li><li><code>/jmconfig cover [on/off]</code> - 控制是否显示封面图片</li></ul><h3 id="域名管理" tabindex="-1">域名管理 <a class="header-anchor" href="#域名管理" aria-label="Permalink to &quot;域名管理&quot;">​</a></h3><ul><li><code>/jmdomain list</code> - 显示当前配置的域名</li><li><code>/jmdomain test</code> - 测试所有域名并显示结果</li><li><code>/jmdomain update</code> - 测试并自动更新可用域名</li></ul><h2 id="◎-使用示例" tabindex="-1">◎ 使用示例 <a class="header-anchor" href="#◎-使用示例" aria-label="Permalink to &quot;◎ 使用示例&quot;">​</a></h2><h3 id="下载与发送漫画" tabindex="-1">下载与发送漫画 <a class="header-anchor" href="#下载与发送漫画" aria-label="Permalink to &quot;下载与发送漫画&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/jm 123456</span></span></code></pre></div><h3 id="获取漫画预览" tabindex="-1">获取漫画预览 <a class="header-anchor" href="#获取漫画预览" aria-label="Permalink to &quot;获取漫画预览&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/jmimg 123456 5</span></span></code></pre></div><p>获取漫画前 5 页预览</p><h3 id="搜索漫画" tabindex="-1">搜索漫画 <a class="header-anchor" href="#搜索漫画" aria-label="Permalink to &quot;搜索漫画&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/jmsearch 关键词 1</span></span></code></pre></div><p>搜索并获取第 1 个结果</p><h3 id="搜索作者作品" tabindex="-1">搜索作者作品 <a class="header-anchor" href="#搜索作者作品" aria-label="Permalink to &quot;搜索作者作品&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/jmauthor そらもち 3</span></span></code></pre></div><p>显示作者前 3 部作品列表</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/jmauthor そらもち 1</span></span></code></pre></div><p>显示作者第 1 部作品的详细信息（包含封面）</p><h3 id="设置-http-代理" tabindex="-1">设置 HTTP 代理 <a class="header-anchor" href="#设置-http-代理" aria-label="Permalink to &quot;设置 HTTP 代理&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/jmconfig proxy http://127.0.0.1:7890</span></span></code></pre></div><h3 id="更新可用域名" tabindex="-1">更新可用域名 <a class="header-anchor" href="#更新可用域名" aria-label="Permalink to &quot;更新可用域名&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/jmdomain update</span></span></code></pre></div>',36)])])}const m=e(t,[["render",s]]);export{u as __pageData,m as default};
